ProgressBar=function(){this.processbar=null;this.element=null;this.parent=null;this.showing=false;this.window=null};ProgressBar.prototype.create=function(){var d=document.createElement("div");d.className="progressbar";d.id="progressbar";this.element=$(d);return this};ProgressBar.prototype.show=function(parent){if(this.showing)return;if(!this.window)this.window=$(window);if(!this.parent)this.parent=$(document.getElementsByTagName("body")[0]);this.parent.append(this.element);this.element.css({top:this.window.scrollTop()+(this.window.height()-this.element.height())/2,left:this.window.scrollLeft()+Math.round(Math.max(this.window.width()-this.element.outerWidth(true),0)/2)});this.showing=true;this.element.show()};ProgressBar.prototype.hide=function(){if(!this.showing)return;this.element.hide();if(this.parent[0].hasChildNodes(this.element[0])){try{this.parent[0].removeChild(this.element[0])}catch(e){}}this.showing=false};ProgressBar.processbar=new ProgressBar().create();ProgressBar.show=function(parent){ProgressBar.processbar.show(parent)};ProgressBar.hide=function(){ProgressBar.processbar.hide()};